import React, { useState } from "react";
import RenderTextInput from "./RenderTextInput";

function PersonalDetails(props) {
  const personal = {
    personalName: {
      placeholder: "Full Name",
      value: "John Jones",
    },
    personalPhone: {
      placeholder: "Phone Number",
      value: "+61 412 345 678",
    },
    personalLocation: {
      placeholder: "Location",
      value: "Sydney, AU",
    },
    personalEmail: {
      placeholder: "Email",
      value: "john.jones@jmail.com",
    },
  };

  const personalDesc = {
    personalD: {
      placeholder: "A short description about yourself",
      value:
        "A software developer who is passionate about delivering the highest quality work and is always looking to improve, adapt and innovate.",
    },
  };

  const [initPerson, setPerson] = useState(personal);
  const [initPersonDesc, setpersonDesc] = useState(personalDesc);

  const handlePerson = (e) => {
    const category = e.target.getAttribute("data-input");
    setPerson({
      ...initPerson,
      [category]: {
        ...initPerson[category],
        value: e.target.value,
      },
    });
  };

  const handleDesc = (e) => {
    const category = e.target.getAttribute("data-input");
    setpersonDesc({
      ...initPersonDesc,
      [category]: {
        ...initPersonDesc[category],
        value: e.target.value,
      },
    });
  };

  return (
    <React.Fragment>
      <RenderTextInput
        index={0}
        passedObj={initPerson}
        handler={handlePerson}
      />
      <textarea
        onChange={handleDesc}
        placeholder={initPersonDesc.placeholder}
        value={initPersonDesc.perovalue}
      ></textarea>
    </React.Fragment>
  );
}

export default PersonalDetails;
