import React, { useState } from "react";
import uniqid from "uniqid";
import WorkResponsibilities from "./WorkResponsibilities";

function WorkExp() {
  const initialWork = {
    company: {
      className: "cool",
      placeholder: "Company",
      value: "Google",
    },
    position: {
      placeholder: "Position",
      value: "Software Engineer",
    },
    start: {
      placeholder: "Start Date",
      value: "2021-2",
    },
    end: {
      placeholder: "End Date",
      value: "2022-2",
    },
  };

  const [work, setWork] = useState(initialWork);
  const [workList, setWorkList] = useState([work]);

  const handleWork = (e) => {
    setWork((prevWork) => {
      return {
        [e.target.name]: {
          ...prevWork[e.target.name],
          value: e.target.value,
        },
      };
    });
  };

  return (
    <div>
      {Object.keys(work).map((category) => {
        return (
          <input
            key={category}
            type="text"
            id={category}
            onChange={handleWork}
            value={work[category].value}
            placeholder={work[category].placeholder}
          ></input>
        );
      })}
    </div>
  );
}

export default WorkExp;
