import React, { Component } from "react";

export class PersonalDetails extends Component {
  constructor() {
    super();
    // this.listReturn = ["pName", "pPhone", "pLocation", "pEmail"];
	  this.state = {
		  pName: {
			  placeholder: "Full Name",
			  value: "John Jones"
		  },
		  pPhone: {
			placeholder: "Full Name",
			value: "John Jones"
		  },
		  pLocation: {
			placeholder: "Full Name",
			value: "John Jones"
		  },
		  pEmail: {
			placeholder: "Full Name",
			value: "John Jones"
		},

        pPhone: "Phone Number",
        pLocation: "Sydney, Australia",
        pEmail: "john.jones@gmail.com",
      placeholder: {

      },
    };
  }

  render() {
    return (
      <div>
        <form>
          {Object.keys(this.state.placeholder).map((key) => {
            return (
              <input
                type="text"
                id={this.state.placeholder.key}
                value={this.state.value.key}
                placeholder={this.state.placeholder.key}
              ></input>
            );
          })}

          <input
            type="text"
            id="pName"
            placeholder="Full Name"
            value={this.state.pName}
          ></input>
          <input
            type="text"
            id="pNumber"
            placeholder="Phone Number"
            value={this.state.pPhone}
          ></input>
          <input
            type="text"
            id="pLocation"
            placeholder="Location"
            value={this.state.pLocation}
          ></input>
          <input
            type="text"
            id="pEmail"
            placeholder="Email"
            value={this.state.pEmail}
          ></input>
        </form>
      </div>
    );
  }
}

export default PersonalDetails;
