import React, { Component } from "react";

export class PersonalDetails extends Component {
  constructor() {
    super();
    this.state.name = "John Jones";
    this.state.phone = "01 2345 6789";
    this.state.location = "Sydney, Australia";
    this.state.email = "john.jones@gmail.com";
  }

  handleName(event) {
    this.setState();
  }

  render() {
    return (
      <div>
        <form>
          <input
            type="text"
            id="pName"
            placeholder="Full Name"
            value={this.state.name}
          ></input>
          <input
            type="text"
            id="pNumber"
            placeholder="Phone Number"
            value={this.state.phone}
          ></input>
          <input
            type="text"
            id="pLocation"
            placeholder="Full Name"
            value={this.state.Location}
          ></input>
          <input
            type="text"
            id="pEmail"
            placeholder="Full Name"
            value={this.state.email}
          ></input>
        </form>
      </div>
    );
  }
}

export default PersonalDetails;
