import React, { useState } from "react";
import PersonalDetails from "./FormComponents/PersonalDetails";
import RenderCard from "./FormComponents/RenderCard";

function ResumeForm(props) {
  const templateEdu = {
    university: {
      placeholder: "University / Institution",
      value: "",
    },
    degree: {
      placeholder: "Degree",
      value: "",
    },
    start: {
      placeholder: "Start Date",
      value: "",
    },
    end: {
      placeholder: "End Date",
      value: "",
    },
  };

  const defaultEdu = {
    ...templateEdu,
    university: {
      value: "University of Sydney",
    },
    degree: {
      value: "Bachelor of Computer Science",
    },
    start: {
      value: "2019-3",
    },
    end: {
      value: "2020-11",
    },
  };

  const templateOther = {
    university: {
      placeholder: "Institution",
      value: "",
    },
    degree: {
      placeholder: "Award / Activity / Others",
      value: "",
    },
    start: {
      placeholder: "Start Date",
      value: "",
    },
    end: {
      placeholder: "End Date",
      value: "",
    },
  };

  const initialOther = {
    ...templateOther,
    university: {
      value: "University of Sydney",
    },
    degree: {
      value: "Dean's List for School of Computer Science 2019",
    },
    start: {
      value: "2019-3",
    },
    end: {
      value: "2019-11",
    },
  };

  const workTemplate = {
    company: {
      placeholder: "Company",
      value: "",
    },
    position: {
      placeholder: "Position",
      value: "",
    },
    start: {
      placeholder: "Start Date",
      value: "",
    },
    end: {
      placeholder: "End Date",
      value: "",
    },
  };

  const initialWork = {
    ...workTemplate,
    company: {
      value: "Google",
    },
    position: {
      value: "Software Engineer",
    },
    start: {
      value: "2021-2",
    },
    end: {
      value: "2022-2",
    },
  };

  const [workNum, addWorkNum] = useState(0);
  const [workList, setWorkList] = useState({ [workNum]: initialWork });


  const [otherNum, addOtherNum] = useState(0);
  const [otherList, setOtherList] = useState({ [workNum]: initialWork });

  const addWork = (e) => {
    setWorkList((prevWorkList) => ({
      ...prevWorkList,
      [workNum + 1]: workTemplate,
    }));
    addWorkNum((prevJobNum) => prevJobNum + 1);
  };

  const changeWork = (e) => {
    const [inputBox, jobIndex] = e.target.name.split("_");
    setWorkList((prevWorkList) => ({
      ...prevWorkList,
      [jobIndex]: {
        ...prevWorkList[jobIndex],
        [inputBox]: {
          ...prevWorkList[jobIndex][inputBox],
          value: e.target.value,
        },
      },
    }));
  };

  const removeWork = (e) => {
    const currWorkName = e.target.parentNode.getAttribute("data-name");
    const [_, jobIndex] = currWorkName.split("_");
    const newObj = {};
    Object.assign(newObj, workList);
    delete newObj[`${jobIndex}`];
    setWorkList(newObj);
  };

  const Resp = {
    placeholder: "Reponsibility",
    value: "",
  };

  const [respNum, addRespNum] = useState(0);
  const [respList, setRespList] = useState({
    [`${workNum}_${respNum}`]: Resp,
  });

  const addResp = () => {
    setRespList((prevRespList) => ({
      ...prevRespList,
      [`${workNum}_${respNum + 1}`]: Resp,
    }));
    addRespNum((prevJobNum) => prevJobNum + 1);
  };

  const removeResp = (e) => {
    const currResp = e.target.previousElementSibling;
    const [_, jobIndex, respIndex] = currResp.name.split("_");
    const newObj = {};
    Object.assign(newObj, respList);
    delete newObj[`${jobIndex}_${respIndex}`];
    setRespList(newObj);
  };

  const changeResp = (e) => {
    const [_, jobIndex, respIndex] = e.target.name.split("_");
    setRespList((prevRespList) => ({
      ...prevRespList,
      [`${jobIndex}_${respIndex}`]: {
        ...prevRespList[`${jobIndex}_${respIndex}`],
        value: e.target.value,
      },
    }));
  };
  const workSection = "work";
  const respSubS = "Responsibility";
  const eduSection = "edu"
  const descSubS = "Description"



  const workObj = { workSection, workList, changeWork, addWork, removeWork };
  const respObj = { respSubS, respList, changeResp, addResp, removeResp };


  const eduObj = {eduSection, workList, changeWork, addWork, removeWork }
  const eduDesc = {descSubS, respList, changeResp, addResp, removeResp }

  return (
    <div id="formDiv">
      <form>
        <div>
          <h2>Personal Details</h2>
          <PersonalDetails />
        </div>
        <div>
          <h2>Work Experience</h2>
          <RenderCard card={workObj} dp={respObj} />
        </div>
        <div>
          <h2>Education</h2>
          <RenderCard card={workObj} dp={respObj} />
        </div>
        <div>
          <h2>Others</h2>
          <RenderCard card={workObj} dp={respObj} />
        </div>
      </form>
    </div>
  );
}

export default ResumeForm;
