import React, { useState } from "react";
import uniqid from "uniqid";
import WorkResponsibilities from "./WorkResponsibilities";
import RenderTextInput from "./RenderTextInput";

function WorkExp() {
  const workTemplate = {
    company: {
      placeholder: "Company",
    },
    position: {
      placeholder: "Position",
    },
    start: {
      placeholder: "Start Date",
    },
    end: {
      placeholder: "End Date",
    },
  };

  const initialWork = {
    ...workTemplate,
    company: {
      value: "Google",
    },
    position: {
      value: "Software Engineer",
    },
    start: {
      value: "2021-2",
    },
    end: {
      value: "2022-2",
    },
  };

  const [workList, setWorkList] = useState([initialWork]);

  const handleWork = (e) => {
    e.preventDefault();
    setWorkList((prevWork) => {
      prevWork.concat(workTemplate);
    });
  };

  return (
    <div>
      {workList.map((work, index) => (
        <RenderTextInput passedObj={work} handler={handleWork} index={index} />
      ))}
      <button>Add</button>
    </div>
  );
}

export default WorkExp;
