import React, { useState} from "react";
import RenderTextInput from "./RenderTextInput";

function PersonalDetails(props) {
  const { personal } = props




  const [initPerson, setPerson] = useState(personal)

  handleChange = (e) => {
    setPerson({
      [e.target.id]: {
        ...this.state,
        value: e.target.value,
      },
    });
  };


  return (
    <React.Fragment>
      <RenderTextInput
        index={0}
        passedObj={this.state}
        handler={this.handleChange}
      />
      <textarea
        onChange={this.handleChange}
        placeholder="A short description about yourself"
        value="A software developer who is passionate about delivering the highest quality work and is always looking to improve, adapt and innovate."
      ></textarea>
    </React.Fragment>
  )
}


export default PersonalDetails
