import React, { useState } from "react";
import RenderTextInput from "./RenderTextInput";

function EduOther(props) {


  const eduTemplate = {
    university: {
      placeholder: "University / Institution",
      value: "",
    },
    degree: {
      placeholder: "Degree",
      value: "",
    },
    start: {
      placeholder: "Start Date",
      value: "",
    },
    end: {
      placeholder: "End Date",
      value: "",
    },
  };

  const initialTemp = {
    ...eduTemplate,
    university: {
      value: "University of Sydney",
    },
    degree: {
      value: "Bachelor of Computer Science",
    },
    start: {
      value: "2019-3",
    },
    end: {
      value: "2020-11",
    },
  };

  const [eduNum, addEduNum] = useState(0);
  const [eduList, setEduList] = useState({ [eduNum]: initialTemp });

  const addWork = (e) => {
    setEduList((prevEduList) => ({
      ...prevEduList,
      [eduNum + 1]: eduTemplate,
    }));
    addEduNum((prevJobNum) => prevJobNum + 1);
  };

  const handleChange = (e) => {
    const [inputBox, eduIndex] = e.target.name.split("_");
    setEduList((prevEduList) => ({
      ...prevEduList,
      [eduIndex]: {
        ...prevEduList[eduIndex],
        [inputBox]: {
          ...prevEduList[eduIndex][inputBox],
          value: e.target.value,
        },
      },
    }));



  };

  return (
    <div>
      {Object.keys(eduList).map((key) => (
        <div className="workExp--block" key={key}>
          <RenderTextInput
            passedObj={eduList[key]}
            handler={handleChange}
            index={key}
          />
          <textarea placeholder="Description"></textarea>
        </div>
      ))}
      <button type="button" onClick={addWork}>
        Add Education
      </button>
    </div>
  );
}

export default EduOther;
