import React, { Component } from "react";

export class PersonalDetails extends Component {
  constructor() {
    super();
    // this.listReturn = ["pName", "pPhone", "pLocation", "pEmail"];
    this.state = {
      pName: {
        placeholder: "Full Name",
        value: "John Jones",
      },
      pPhone: {
        placeholder: "Phone Number",
        value: "+61 412 345 678",
      },
      pLocation: {
        placeholder: "Location",
        value: "Sydney, AU",
      },
      pEmail: {
        placeholder: "Email",
        value: "john.jones@jmail.com",
      },
    };
  }

  handleChange = (e) => {
    this.setState({
      pName: {
        placeholder: this.state.pName.placeholder,
        value: e.target.name,
      },
	});

  };

  render() {
    return (
      <div>
        <form>
          {Object.keys(this.state).map((category) => {
            return (
              <input
                type="text"
                id={category}
                onChange={this.handleChange}
                value={this.state[category].value}
                placeholder={this.state[category].placeholder}
              ></input>
            );
          })}
        </form>
      </div>
    );
  }
}

export default PersonalDetails;
