import React, { Component } from "react";
import uniqid from "uniqid";
import WorkResponsibilities from "./WorkResponsibilities";

export class WorkExp extends Component {
  constructor() {
    super();
    this.state = {
      work: {
        company: {
          placeholder: "Company",
          value: "Google",
        },
        position: {
          placeholder: "Position",
          value: "Software Engineer",
        },
        start: {
          value: "2021-2",
        },
        end: {
          value: "2022-2",
        },
        responsibilities: {
          placeholder: [],
          value: [],
        },
      },
      works: [],
    };
  }

  addWorkExp = (e) => {
    this.setState({
      works: this.state.works.concat(this.state.work),
      work: {
        company: {
          className: "cool",
          placeholder: "Company",
          value: "Google",
        },
        position: {
          className: null,
          placeholder: "Position",
          value: "Software Engineer",
        },
        start: {
          className: null,
          placeholder: "Start Date",
          value: "2021-02",
        },
        end: {
          className: null,
          placeholder: "End Date",
          value: "2022-02",
        },
      },
    });
  };

  handleChange = (e) => {
    this.setState({
      [e.target.id]: {
        placeholder: this.state[e.target.id].placeholder,
        value: e.target.value,
      },
    });
  };

  render() {
    return (
      <div>
        <input
          type="text"
          className="{this.state.work.company.className"
          placeholder={this.state.work.company.placeholder}
          value={this.state.work.company.value}
        ></input>
        <input
          type="text"
          placeholder={this.state.work.position.placeholder}
          value={this.state.work.position.value}
        ></input>
        <input
          type="text"
          onChange={this.handleChange}
          placeholder={this.state.work.start.placeholder}
          defaultValue={this.state.work.start.value}
        ></input>
        <input
          type="text"
          onChange={this.handleChange}
          placeholder={this.state.work.end.placeholder}
          defaultValue={this.state.work.end.value}
        ></input>
        <WorkResponsibilities />
      </div>
    );
  }
}

export default WorkExp;
