import React, { Component } from "react";
import uniqid from "uniqid";

export class WorkExp extends Component {
  constructor() {
    super();
    this.state = {
      work: {
        Company: {
          placeholder: "Company",
          value: "Google",
        },
        Position: {
          placeholder: "Position",
          value: "Software Engineer",
        },
        Start: {
          value: "2021-2",
        },
        End: {
          value: "2022-2",
        },
        Responsibilities: {
          placeholder: [],
          value: [],
        },
      },
      works: [],
    };
  }

	addWorkExp = (e) => {
		this.setState({
		  works: this.state.works.concat(this.state.work)
	  })
  };
  handleChange = (e) => {
    this.setState({
      [e.target.id]: {
        placeholder: this.state[e.target.id].placeholder,
        value: e.target.value,
      },
    });
  };

  render() {
    return <div></div>;
  }
}

export default WorkExp;
