import React, { useState } from "react";
import uniqid from "uniqid";
import WorkResponsibilities from "./WorkResponsibilities";

function WorkExp() {
  const initialWork = {
    company: {
      className: "cool",
      placeholder: "Company",
      value: "Google",
    },
    position: {
      placeholder: "Position",
      value: "Software Engineer",
    },
    start: {
      value: "2021-2",
    },
    end: {
      value: "2022-2",
    },
    responsibilities: {
      placeholder: [],
      value: [],
    },
  };

  const [work, setWork] = useState(initialWork);
  const [workList, setWorkList] = useState([work]);

  const addWorkExp = (e) => {
    this.setState({
      ...this.state,
      works: this.state.works.concat(this.state.work),
    });
  };

  const handleChange = (e) => {
    this.setState({
      [e.target.id]: {
        placeholder: this.state[e.target.id].placeholder,
        value: e.target.value,
      },
    });
  };

  return (
    <div>
      {Object.keys(this.state).map((category) => {
        return (
          <input
            key={category}
            type="text"
            id={category}
            onChange={this.handleChange}
            value={this.state[category].value}
            placeholder={this.state[category].placeholder}
          ></input>
        );
      })}
    </div>
  );
}

export default WorkExp;
