import React, { useState } from "react";
import uniqid from "uniqid";
import WorkResponsibilities from "./WorkResponsibilities";
import RenderTextInput from "./RenderTextInput";

function WorkExp() {
  let jobCounter = 0;
  const workTemplate = {
    company: {
      placeholder: "Company",
    },
    position: {
      placeholder: "Position",
    },
    start: {
      placeholder: "Start Date",
    },
    end: {
      placeholder: "End Date",
    },
  };

  const initialWork = {
    ...workTemplate,
    company: {
      value: "Google",
    },
    position: {
      value: "Software Engineer",
    },
    start: {
      value: "2021-2",
    },
    end: {
      value: "2022-2",
    },
  };

  const [workObject, setWorkObject] = useState({ jobCounter: initialWork });

  const handleWork = (e) => {
    e.preventDefault();
    setWorkObject((prevWork) => {
      workO: { }
      prevWork.jobCounter = workTemplate;
    });
  };

  const handleChange = (e) => {
    console.log(e.target);
  };

  return (
    <div>
      {Object.keys(workObject).map((jobNumber, index) => (
        <RenderTextInput
          passedObj={workObject[jobNumber]}
          handler={handleChange}
          index={index}
        />
      ))}
      <button type="button" onClick={handleWork}>
        Add
      </button>
    </div>
  );
}

export default WorkExp;
