import React, { Component } from "react";

export class PersonalDetails extends Component {
  constructor() {
    super();
    // this.listReturn = ["pName", "pPhone", "pLocation", "pEmail"];
    this.state = {
      pName: {
        placeholder: "Full Name",
        value: "John Jones",
      },
      pPhone: {
        placeholder: "Full Name",
        value: "John Jones",
      },
      pLocation: {
        placeholder: "Full Name",
        value: "John Jones",
      },
      pEmail: {
        placeholder: "Full Name",
        value: "John Jones",
      },
    };
  }

  render() {
    return (
      <div>
        <form>
          {Object.keys(this.state).map((category) => {
            return (
              <input
                type="text"
                id={category}
                value={this.state[category]}
                placeholder={category}
              ></input>
            );
          })}
        </form>
      </div>
    );
  }
}

export default PersonalDetails;
