import React, { useState } from "react";
import PersonalDetails from "./FormComponents/PersonalDetails";
import RenderCard from "./FormComponents/RenderCard";

function ResumeForm() {
  const handleAddCard = (template, count, counter, setter) => {
    setter((prevList) => ({
      ...prevList,
      [count + 1]: template,
    }));
    counter((prevNum) => prevNum + 1);
  };

  const handleChangeCard = (e, setter) => {
    const [inputBox, cardIndex] = e.target.name.split("_");
    setter((prevList) => ({
      ...prevList,
      [cardIndex]: {
        ...prevList[cardIndex],
        [inputBox]: {
          ...prevList[cardIndex][inputBox],
          value: e.target.value,
        },
      },
    }));
  };

  const handleRemoveCard = (e, currList, setter) => {
    const currWorkName = e.target.parentNode.getAttribute("data-name");
    const [_, cardIndex] = currWorkName.split("_");
    const newObj = {};
    Object.assign(newObj, currList);
    delete newObj[`${cardIndex}`];
    setter(newObj);
  };

  const HandleAddDP = (cardNum, dpNum, dpSetter, dpCounter, dpTemplate) => {
    dpSetter((prevDPList) => ({
      ...prevDPList,
      [`${cardNum}_${dpNum + 1}`]: dpTemplate,
    }));
    dpCounter((prevDPNum) => prevDPNum + 1);
  };

  const HandleRemoveDP = (e, currDPList, dpSetter) => {
    const currDP = e.target.previousElementSibling;
    const [_, cardIndex, dpIndex] = currDP.name.split("_");
    const newObj = {};
    Object.assign(newObj, currDPList);
    delete newObj[`${cardIndex}_${dpIndex}`];
    dpSetter(newObj);
  };

  const HandleChangeDP = (e, dpSetter) => {
    const [_, cardIndex, dpIndex] = e.target.name.split("_");
    dpSetter((prevDPList) => ({
      ...prevDPList,
      [`${cardIndex}_${dpIndex}`]: {
        ...prevDPList[`${cardIndex}_${dpIndex}`],
        value: e.target.value,
      },
    }));
  };

  const tempEdu = {
    university: {
      placeholder: "University / Institution",
      value: "",
    },
    degree: {
      placeholder: "Degree",
      value: "",
    },
    start: {
      placeholder: "Start Date",
      value: "",
    },
    end: {
      placeholder: "End Date",
      value: "",
    },
  };

  const initEdu = {
    ...tempEdu,
    university: {
      value: "University of Sydney",
    },
    degree: {
      value: "Bachelor of Computer Science",
    },
    start: {
      value: "2019-3",
    },
    end: {
      value: "2020-11",
    },
  };

  const tempOther = {
    institution: {
      placeholder: "Institution",
      value: "",
    },
    detail: {
      placeholder: "Award / Activity / Others",
      value: "",
    },
    start: {
      placeholder: "Start Date",
      value: "",
    },
    end: {
      placeholder: "End Date",
      value: "",
    },
  };

  const initOther = {
    ...tempOther,
    institution: {
      value: "University of Sydney",
    },
    detail: {
      value: "Dean's List for School of Computer Science 2019",
    },
    start: {
      value: "2019-3",
    },
    end: {
      value: "2019-11",
    },
  };

  const tempWork = {
    company: {
      placeholder: "Company",
    },
    position: {
      placeholder: "Position",
      value: "",
    },
    start: {
      placeholder: "Start Date",
      value: "",
    },
    end: {
      placeholder: "End Date",
      value: "",
    },
  };

  const initWork = {
    ...tempWork,
    company: {
      value: "Google",
    },
    // position: {
    //   value: "Software Engineer",
    // },
    // start: {
    //   value: "2021-2",
    // },
    // end: {
    //   value: "2022-2",
    // },
  };

  const [workNum, addWorkNum] = useState(0);
  const [workList, setWorkList] = useState({ [workNum]: initWork });

  console.log(workList);

  const [eduNum, addEduNum] = useState(0);
  const [eduList, setEduList] = useState({ [eduNum]: initEdu });

  const [otherNum, addOtherNum] = useState(0);
  const [otherList, setOtherList] = useState({ [otherNum]: initOther });

  const addWork = () =>
    handleAddCard(tempWork, workNum, addWorkNum, setWorkList);
  const changeWork = (e) => handleChangeCard(e, setWorkList);
  const removeWork = (e) => handleRemoveCard(e, workList, setWorkList);

  const addEdu = () => handleAddCard(tempEdu, eduNum, addEduNum, setEduList);
  const changeEdu = (e) => handleChangeCard(e, setEduList);
  const removeEdu = (e) => handleRemoveCard(e, eduList, setEduList);

  const addOther = () =>
    handleAddCard(tempOther, otherNum, addOtherNum, setOtherList);
  const changeOther = (e) => handleChangeCard(e, setOtherList);
  const removeOther = (e) => handleRemoveCard(e, otherList, setOtherList);

  const tempResp = { placeholder: "Reponsibility" };
  const tempDesc = { placeholder: "Description" };

  const [respNum, addRespNum] = useState(0);
  const [respList, setRespList] = useState({
    [`${workNum}_${respNum}`]: tempResp,
  });

  const [eduDescNum, addEduDescNum] = useState(0);
  const [eduDescList, setEduDescList] = useState({
    [`${eduNum}_${eduDescNum}`]: tempDesc,
  });

  const [otherDescNum, addOtherDescNum] = useState(0);
  const [otherDescList, setOtherDescList] = useState({
    [`${otherNum}_${otherDescNum}`]: tempDesc,
  });

  const addResp = () =>
    HandleAddDP(workNum, respNum, setRespList, addRespNum, tempResp);
  const removeResp = (e) => HandleRemoveDP(e, respList, setRespList);
  const changeResp = (e) => HandleChangeDP(e, setRespList);

  const addEduDesc = () =>
    HandleAddDP(eduNum, eduDescNum, setEduDescList, addEduDescNum, tempDesc);
  const removeEduDesc = (e) => HandleRemoveDP(e, eduDescList, setEduDescList);
  const changeEduDesc = (e) => HandleChangeDP(e, setEduDescList);

  const addOtherDesc = () =>
    HandleAddDP(
      otherNum,
      otherDescNum,
      setOtherDescList,
      addOtherDescNum,
      tempDesc
    );
  const removeOtherDesc = (e) =>
    HandleRemoveDP(e, otherDescList, setOtherDescList);
  const changeOtherDesc = (e) => HandleChangeDP(e, setOtherDescList);

  const workObj = {
    sectionName: "Work",
    cardList: workList,
    changeCard: changeWork,
    addCard: addWork,
    removeCard: removeWork,
  };
  const respObj = {
    subsecName: "Responsibility",
    dpList: respList,
    changeDP: changeResp,
    addDP: addResp,
    removeDP: removeResp,
  };

  const eduObj = {
    sectionName: "Education",
    cardList: eduList,
    changeCard: changeEdu,
    addCard: addEdu,
    removeCard: removeEdu,
  };
  const eduDescObj = {
    subsecName: "Description",
    dpList: eduDescList,
    changeDP: changeEduDesc,
    addDP: addEduDesc,
    removeDP: removeEduDesc,
  };

  const otherObj = {
    sectionName: "Other",
    cardList: otherList,
    changeCard: changeOther,
    addCard: addOther,
    removeCard: removeOther,
  };
  const otherDescObj = {
    subsecName: "Details",
    dpList: otherDescList,
    changeDP: changeOtherDesc,
    addDP: addOtherDesc,
    removeDP: removeOtherDesc,
  };

  return (
    <div id="formDiv">
      <form>
        <div>
          <h2>Personal Details</h2>
          <PersonalDetails />
        </div>
        <div>
          <h2>Work Experience</h2>
          <RenderCard card={workObj} dp={respObj} />
        </div>
        <div>
          <h2>Education</h2>
          <RenderCard card={eduObj} dp={eduDescObj} />
        </div>
        <div>
          <h2>Others</h2>
          <RenderCard card={otherObj} dp={otherDescObj} />
        </div>
      </form>
    </div>
  );
}

export default ResumeForm;
