import React, { Component } from "react";

export class PersonalDetails extends Component {
  constructor() {
    super();
    // this.listReturn = ["pName", "pPhone", "pLocation", "pEmail"];
    this.state = {
      placeholder: {
        pName: "Full Name",
        pPhone: "01 2345 6789",
        pLocation: "Sydney, Australia",
        pEmail: "john.jones@gmail.com",
      },
      value: {
        pName: "John Jones",
        pPhone: "01 2345 6789",
        pLocation: "Sydney, Australia",
        pEmail: "john.jones@gmail.com",
      },
    };
  }

  render() {
    return (
      <div>
        <form>
          {Object.keys(this.state.placeholder).map((key) => (
            <input
              type="text"
              id={this.state.placeholder.key}
              value={this.state.placeholderkey}
            ></input>
          ))}

          <input
            type="text"
            id="pName"
            placeholder="Full Name"
            value={this.state.pName}
          ></input>
          <input
            type="text"
            id="pNumber"
            placeholder="Phone Number"
            value={this.state.pPhone}
          ></input>
          <input
            type="text"
            id="pLocation"
            placeholder="Location"
            value={this.state.pLocation}
          ></input>
          <input
            type="text"
            id="pEmail"
            placeholder="Email"
            value={this.state.pEmail}
          ></input>
        </form>
      </div>
    );
  }
}

export default PersonalDetails;
