import React, { Component } from "react";

export class PersonalDetails extends Component {
  constructor() {
    super();
    this.state.name = "John Jones";
    this.state.number = "01 2345 6789";
    this.state.location = "Sydney, Australia";
    this.state.email = "john.jones@gmail.com";
  }

  handleName(event) {
    this.setState();
  }

  render() {
    return (
      <div>
        <form>

          <input
            type="text"
            id="personName"
            placeholder="Full Name"
            value={this.state.name}
          ></input>
        </form>
      </div>
    );
  }
}

export default PersonalDetails;
