import React, { Component } from "react";
import uniqid from "uniqid"

export class WorkExp extends Component {
  constructor() {
    super();
    this.state = {
      work : {
        placeholder: "Company",
        value: "Google",
        startDate: "",
        endDate: "",
		},
		works: []
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.id]: {
        placeholder: this.state[e.target.id].placeholder,
        value: e.target.value,
      },
    });
  };


  render() {
    return (
      <div>
        {Object.keys(this.state).map((category) => {
          return (
            <input
              key={category}
              type="text"
              id={category}
              onChange={this.handleChange}
              value={this.state[category].value}
              placeholder={this.state[category].placeholder}
            ></input>
          );
        })}
      </div>
    );
  }
}

export default WorkExp;
