import React, { Component } from "react";

export class PersonalDetails extends Component {
  constructor() {
    super();
    this.state = {
      details: {
        pName: {
          placeholder: "Full Name",
          value: "John Jones",
        },
        pPhone: {
          placeholder: "Phone Number",
          value: "+61 412 345 678",
        },
        pLocation: {
          placeholder: "Location",
          value: "Sydney, AU",
        },
        pEmail: {
          placeholder: "Email",
          value: "john.jones@jmail.com",
        },
      },
      description: {
        placeholder: "",
        value: "",
      },
    };
  }

  handleChange = (e) => {
    this.setState({
      [e.target.id]: {
        placeholder: this.stat[e.target.id].placeholder,
        value: e.target.value,
      },
    });
  };

  render() {
    return (
      <div>
        <form>
          {Object.keys(this.state).map((category) => {
            if (category === "details") {
              return (
                <input
                  type="text"
                  id={category}
                  onChange={this.handleChange}
                  value={this.state[category].value}
                  placeholder={this.state[category].placeholder}
                ></input>
              );
            }
          })}
        </form>
      </div>
    );
  }
}

export default PersonalDetails;
