import React, { Component } from "react";
import uniqid from "uniqid";

export class WorkExp extends Component {
  constructor() {
    super();
    this.state = {
      work: {
        company: {
          placeholder: "Company",
          value: "Google",
        },
        position: {
          placeholder: "Position",
          value: "Software Engineer",
        },
        start: {
          value: "2021-2",
        },
        end: {
          value: "2022-2",
        },
        responsibilities: {
          placeholder: [],
          value: [],
        },
      },
      works: [],
    };
  }

  addWorkExp = (e) => {
    this.setState({
      works: this.state.works.concat(this.state.work),
      work: {
        company: {
          placeholder: "Company",
          value: "Google",
        },
        position: {
          placeholder: "Position",
          value: "Software Engineer",
        },
        start: {
          value: "2021-2",
        },
        end: {
          value: "2022-2",
        },
        responsibilities: {
          placeholder: [],
          value: [],
        },
      },
    });
  };

  handleChange = (e) => {
    this.setState({
      [e.target.id]: {
        placeholder: this.state[e.target.id].placeholder,
        value: e.target.value,
      },
    });
  };

  render() {
    return (
      <div>
        <input
          type="text"
          placeholder={this.state.work.company.placeholder}
        ></input>
      </div>
    );
  }
}

export default WorkExp;
