import React, { useState } from "react";
import RenderTextInput from "./RenderTextInput";

function WorkResponsibilities(props) {
  const { jobNumber } = props;
  const Resp = {
    placeholder: "Reponsibility",
    value: "",
  };

  const initialResp = {
    ...Resp,
    value: "Involved in the implementation of the new feature on Chrome.",
  };

  const [respNum, addRespNum] = useState(0);
  const [respList, setRespList] = useState({ [respNum]: initialResp });

  const addWork = (e) => {
    setRespList((prevRespList) => ({
      ...prevRespList,
      [respNum + 1]: Resp,
    }));
    addRespNum((prevJobNum) => prevJobNum + 1);
  };

  const handleChange = (e) => {
    const [inputBox, jobIndex, respIndex] = e.target.name.split("_");
    setRespList((prevWorkList) => ({
      ...prevWorkList,
      [jobIndex]: {
        ...prevWorkList[jobIndex],
        [inputBox]: {
          ...prevWorkList[jobIndex][inputBox],
          value: e.target.value,
        },
      },
    }));
  };

  return (
    <React.Fragment>
      {Object.keys(respList).map((key, index) => (
        <input
          placeholder={respList[key].placeholder}
          value = {respList[key].value}
          onClick={handleChange}
          index={`${jobNumber}_${index}`}
        />
      ))}
      <button type="button" onClick={addWork}>
        Add
      </button>
    </React.Fragment>
  );
}

export default WorkResponsibilities;
